.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC8_Start
;
;  DESCRIPTION:
;     Applies power setting to the module's analog PSoc block.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;    A contains the power setting 0-3
;
;  RETURNS:  None
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.    
;
 DUALADC8_Start:
_DUALADC8_Start:
 DUALADC8_SetPower:
_DUALADC8_SetPower:
   RAM_PROLOGUE RAM_USE_CLASS_2
   and   A,DUALADC8_bfPOWERMASK                      ; Mask only the valid power setting bits
   mov   X, SP                                       ; Get location of next location on stack
   push  A                                           ; Save power value on temp location

                                                     ; Set power for ADC1
   mov   A, reg[DUALADC8_bfADC1cr3]                  ; Get current value of ADC1cr3
   and   A, ~DUALADC8_bfPOWERMASK                    ; Mask off old power value
   or    A, [X]                                      ; OR in new power value
   or    A, f0h                                      ; Make sure other register is set correctly
   mov   reg[DUALADC8_bfADC1cr3], A                  ; Reload CR with new power value

                                                     ; Set power for ADC2
   mov   A, reg[DUALADC8_bfADC2cr3]                  ; Get current value of ADC2cr3
   and   A, ~DUALADC8_bfPOWERMASK                    ; Mask off old power value
   or    A, [X]                                      ; OR in new power value
   or    A, f0h                                      ; Make sure other register is set correctly
   mov   reg[DUALADC8_bfADC2cr3], A                  ; Reload CR with new power value

   pop   A                                           ; Restore the stack and power value
   RAM_EPILOGUE RAM_USE_CLASS_2
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC8_Stop
;
;  DESCRIPTION:
;     Removes power from the module's analog PSoc block, but the digital
;     blocks keep on running.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:  None
;
;  RETURNS: None
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;
 DUALADC8_Stop:
_DUALADC8_Stop:
   RAM_PROLOGUE RAM_USE_CLASS_1
   and   reg[DUALADC8_bfADC1cr3], ~DUALADC8_bfPOWERMASK
   and   reg[DUALADC8_bfADC2cr3], ~DUALADC8_bfPOWERMASK
   RAM_EPILOGUE RAM_USE_CLASS_1
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC8_GetSamples
;
;  DESCRIPTION:
;     Starts the A/D convertor and will place data is memory.  A flag
;     is set whenever a new data value is available.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;     None:
;
;  RETURNS:  None
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;          
;    Currently only the page pointer registers listed below are modified: 
;          CUR_PP
;
 DUALADC8_GetSamples:
_DUALADC8_GetSamples:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC8_iCalcTime
   call  DUALADC8_StopAD                                   ; Halt PWM and counter, Disable
                                                           ; Interrupts, Put A/D in reset

   mov   reg[DUALADC8_bPWM_IntTime_LSB], 00h
   mov   reg[DUALADC8_bPWM_IntTime_MSB], 0x04

                                                   ; Load the CalcTime into the PWM Period
   mov   A,[DUALADC8_iCalcTime+LowByte]
   mov   reg[DUALADC8_bPWM_Period_LSB],A
   mov   A,0x4
   add   A,[DUALADC8_iCalcTime+HighByte]
   mov   reg[DUALADC8_bPWM_Period_MSB],A


DUALADC8_LoadMSBCounter:                                   ; The PWM has been setup by SetResolution, now set the upper
                                                           ; counter which will be the same as the period.
                                                           ; Reset MSB of counter to most negative value
   RAM_SETPAGE_CUR >DUALADC8_cCounter1U
IF (DUALADC8_DATA_FORMAT)
   mov   [DUALADC8_cCounter1U], 0xFE                       ; Start counter1 at most negative signed value
   mov   [DUALADC8_cCounter2U], 0xFE                       ; Start counter2 at most negative signed value
ELSE
   mov   [DUALADC8_cCounter1U], 00h                        ; Always start at zero for unsigned values for ADC1
   mov   [DUALADC8_cCounter2U], 00h                        ; Always start at zero for unsigned values for ADC2
ENDIF
   mov   A, reg[DUALADC8_bPWM_IntTime_LSB]                 ; Dummy Read  - required do not remove
   mov   reg[DUALADC8_bPeriod1], FFh                       ; Make sure counter1 starts at FF
   mov   reg[DUALADC8_bPeriod2], FFh                       ; Make sure counter2 starts at FF

   and   reg[DUALADC8_bfADC1cr3],~DUALADC8_fFSW0           ; Take Integrator out of reset
   and   reg[DUALADC8_bfADC2cr3],~DUALADC8_fFSW0           ; Take Integrator out of reset
IF DUALADC8_NoAZ
   and   reg[DUALADC8_bfADC1cr2],~DUALADC8_fAutoZero       ; Take Integrator out of AutoZero
   and   reg[DUALADC8_bfADC2cr2],~DUALADC8_fAutoZero       ; Take Integrator out of AutoZero
ENDIF

                                                               ; Enable the A/D and Start it!
   or    reg[DUALADC8_bCounter1_CR0], (DUALADC8_fDBLK_ENABLE|DUALADC8_fPULSE_WIDE)   ; Enable the Counter
   or    reg[DUALADC8_bCounter2_CR0], (DUALADC8_fDBLK_ENABLE|DUALADC8_fPULSE_WIDE)   ; Enable the Counter
   or    reg[DUALADC8_fPWM_LSB_CR0], DUALADC8_fDBLK_ENABLE          ; Enable PWM
   or    reg[DUALADC8_bfPWM16_INT_REG], DUALADC8_bfPWM16_Mask      ; Enable PWM and Counter interrupts
   or    reg[DUALADC8_bfCounter1_INT_REG], DUALADC8_bfCounter1_Mask
   or    reg[DUALADC8_bfCounter2_INT_REG], DUALADC8_bfCounter2_Mask
   RAM_EPILOGUE RAM_USE_CLASS_4
   ret
.ENDSECTION

.SECTION
;-----------------------------------------------------------------------------
;  FUNCTION NAME: DUALADC8_StopAD
;
;  DESCRIPTION:
;     Completely shuts down the A/D is an orderly manner.  Both the
;     Timer and Counter are disabled and their interrupts are deactivated.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:
;
;  RETURNS:
;
;  SIDE EFFECTS:
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;
 DUALADC8_StopAD:
_DUALADC8_StopAD:
   RAM_PROLOGUE RAM_USE_CLASS_1
   and   reg[DUALADC8_fPWM_LSB_CR0], ~DUALADC8_fDBLK_ENABLE    ; Disable the PWM

   and   reg[DUALADC8_bCounter1_CR0], ~DUALADC8_fDBLK_ENABLE           ; Disable the Counter
   and   reg[DUALADC8_bCounter2_CR0], ~DUALADC8_fDBLK_ENABLE           ; Disable the Counter

IF DUALADC8_NoAZ
   or    reg[DUALADC8_bfADC1cr2], DUALADC8_fAutoZero           ; Put the Integrator into Autozero mode
   or    reg[DUALADC8_bfADC2cr2], DUALADC8_fAutoZero           ; Put the Integrator into Autozero mode
ENDIF

   or   reg[DUALADC8_bfADC1cr3], DUALADC8_fFSW0                ; Put Integrator into reset
   or   reg[DUALADC8_bfADC2cr3], DUALADC8_fFSW0                ; Put Integrator into reset

   and   reg[DUALADC8_bfPWM16_INT_REG], ~DUALADC8_bfPWM16_Mask      ; Disable interrupts
   and   reg[DUALADC8_bfCounter1_INT_REG], ~DUALADC8_bfCounter1_Mask
   and   reg[DUALADC8_bfCounter2_INT_REG], ~DUALADC8_bfCounter2_Mask
   RAM_EPILOGUE RAM_USE_CLASS_1
   ret
.ENDSECTION

