;
 DUALADC8_fIsData:
_DUALADC8_fIsData:
 DUALADC8_fIsDataAvailable:
_DUALADC8_fIsDataAvailable:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >DUALADC8_bfStatus
   mov   A, [DUALADC8_bfStatus]                       ; Get status byte
   and   A, DUALADC8_fDATA_READY                      ; Mask off other bits
   RAM_EPILOGUE RAM_USE_CLASS_4
